import React from "react";
import { Link, withRouter } from "react-router-dom";
import facebookLogo from "./../images/facebook.png";
import CircularIcon from "./CircularIcon.jsx";

const styles = {
  barStyles:{
    display: "flex",
    justifyContent: "space-between",
    height: "60px",
    backgroundColor: "white",
    boxShadow: "0px 1px 4px #c5c5c5",
    padding: "10px 10px 10px 10px"
  },
  iconStyles: {
    display: "flex",
  }
};

function TopBar(props) {
  return (
    <div className="navigation">
      <nav style = {styles.barStyles}>
        <img src = {facebookLogo} width = "40" height = "40"></img>
        <Link class="navbar-brand" to="/">
          Facebook Lite
        </Link>
        <div style = {styles.iconStyles}>
          <CircularIcon type = "plus"/>
          <CircularIcon type = "messenger"/>
          <CircularIcon type = "notifications"/>
          <CircularIcon type = "triangle"/>
        </div>
        <div>
          <ul class="navbar-nav ml-auto">
            <li
              class={`nav-item  ${
                props.location.pathname === "/pages/Home" ? "active" : ""
              }`}
            >
            </li>
          </ul>
        </div>
      </nav>
    </div>
  );
}

export default withRouter(TopBar);
